#include "RFID.h"#include "Arduino.h"RFID::RFID(int SS_PIN, int RST_PIN) {  _SS_PIN = SS_PIN;  _RST_PIN = RST_PIN;  MFRC522 mfrc522(_SS_PIN, _RST_PIN); // Create MFRC522 instance.  _mfrc522 = mfrc522;}void RFID::startRead() {  SPI.begin();      // Inicia  SPI bus  _mfrc522.PCD_Init();   // Inicia MFRC522}int RFID::hasCard(){  // Look for new cards  if ( ! _mfrc522.PICC_IsNewCardPresent())   {    return 0;  }  // Select one of the cards  if ( ! _mfrc522.PICC_ReadCardSerial())   {    return 0;  }  return 1;}int RFID::getSizeID(){  return _mfrc522.uid.size ;}byte* RFID::getCardID(){  return _mfrc522.uid.uidByte;}