#include "RFID.h"#include "Arduino.h"RFID::RFID(int SS_PIN, int RST_PIN) {  _SS_PIN = SS_PIN;  _RST_PIN = RST_PIN;  MFRC522 mfrc522(_SS_PIN, _RST_PIN); // Create MFRC522 instance.  _mfrc522 = mfrc522;}void RFID::startRead() {  SPI.begin();      // Inicia  SPI bus  _mfrc522.PCD_Init();   // Inicia MFRC522}int RFID::hasCard(){  // Look for new cards  if ( ! _mfrc522.PICC_IsNewCardPresent())   {    return 0;  }  // Select one of the cards  if ( ! _mfrc522.PICC_ReadCardSerial())   {    return 0;  }  return 1;}String RFID::getCardID(){  String cardID = "";  for (byte i = 0; i < _mfrc522.uid.size; i++)   {     cardID.concat(String(_mfrc522.uid.uidByte[i] < 0x10 ? " 0" : " "));     cardID.concat(String(_mfrc522.uid.uidByte[i], HEX));  }  cardID.toUpperCase();  return cardID;}