#include "RingTone.h"#include "Arduino.h"RingTone::RingTone(int buzzer) {  _buzzer = buzzer;  pinMode(_buzzer, OUTPUT); // seta o pino onde est√° o buzzer como saida}void RingTone::play(Melody melody){  int pin = _buzzer;  int* tones = melody.tones();  int* noteDurations = melody.duration();  int sizeMelody = melody.sizeMelody();    for (int thisNote = 0; thisNote < sizeMelody; thisNote++) {    // to calculate the note duration, take one second divided by the note type.    //e.g. quarter note = 1000 / 4, eighth note = 1000/8, etc.    int noteDuration = 1000 / noteDurations[thisNote];    tone(pin, tones[thisNote], noteDuration);    // to distinguish the notes, set a minimum time between them.    // the note's duration + 30% seems to work well:    int pauseBetweenNotes = noteDuration * 1.30;    delay(pauseBetweenNotes);    // stop the tone playing:    noTone(pin);  }}void RingTone::error(){  int tones[] = {    NOTE_C4, NOTE_C4  };  int durations[] = {    8, 4  };    int sizeMelody = 2;  Melody errorMelody(tones, durations, sizeMelody);  play(errorMelody);  }void RingTone::success(){  int tones[] = {    NOTE_D4, NOTE_D5, NOTE_D4, NOTE_D5  };    int durations[] = {    12, 12, 12, 12  };  int sizeMelody = 4;    Melody successMelody(tones, durations, sizeMelody);  play(successMelody);  }